class VisitorCounter{constructor(){this.element=document.getElementById("visitor-number"),this.updateCount(),setInterval(()=>this.updateCount(),3e5)}async updateCount(){try{let t=await fetch("counter.php"),e=await t.json();"success"===e.status?this.element.textContent=e.count:console.error("Update failed:",e.message)}catch(n){console.error("Fetch error:",n)}}}document.addEventListener("DOMContentLoaded",()=>new VisitorCounter);